import * as React from 'react'
import {Text , View , Button ,StyleSheet,Modal,TextInput ,TouchableOpacity ,KeyboardAvoidingView, Alert} from 'react-native'
import Icon from 'react-native-vector-icons/AntDesign'
import InputField from '../components/Auth/InputFileld'
import * as firebase from 'firebase'
export default class SignUp extends React.Component{
    state={
        Login:false,
        Email:"",
        Password:"",
        text1:"",
        text2:"",
        user:{},
        token:null,
        user:null,
        error:false,
        errMsg:null
    }
create_user=()=>{


firebase.auth().createUserWithEmailAndPassword(this.state.text1,this.state.text2).catch((error)=>{
    let errorCode = error.code;
    let errorMessage = error.message;
    console.log(errorMessage)

    this.AlertBox(errorMessage)

})
this.setState(
{
 text1:"",
text2:"",})


}
AlertBox=(errMsg)=>{
        Alert.alert(
            "Error",
            errMsg,
            [{
                text:"Ok",

            }],
            {cancelable:true}
        )
}


Login_user=()=>{



    firebase.auth().signInWithEmailAndPassword(this.state.text1, this.state.text2)
    .then(()=>{
        console.log("thanx")
        this.setState({text1:"",text2:""}) 
    }).catch((error) =>{
        let errorCode = error.code;
        let errorMessage = error.message;
        this.AlertBox(errorMessage)

     })

    }


render(){
    const Heading =this.state.Login ? 'SignIn' : "Create New Account"
    return(
        <KeyboardAvoidingView style={styles.mainContainer} behavior={'height'}>
            <View style={styles.Inputcontaier}>
                <View style={{flex:0.2, alignItems:"center", borderBottomColor:"white", borderBottomWidth:2}}> 
                <Text style={{color:'white', fontSize:20}}> {Heading} </Text>
                 </View>
            <View style={{flex:0.5}}>

        <TextInput placeholder={'Email'} placeholderTextColor="grey"  style={styles.InputField} 
        onChangeText={text1=>{this.setState({text1})}}
        keyboardType={"email-address"} value={this.state.text1} returnKeyType={"next"}/>
         <TextInput placeholder={'Password'} placeholderTextColor="grey" style={styles.InputField} 
         secureTextEntry={true} value={this.state.text2} returnKeyType={'done'}
         onChangeText={text2=>{this.setState({text2})}}
         />

            </View>

            {
                !this.state.Login?(

                    <View style={{alignItems:"center",justifyContent:"space-around", padding:30}}>
                    <TouchableOpacity onPress={this.create_user} underlayColor="white">
                        <Text style={{fontSize:20, color:'white'}}>
                            SignUp
                        </Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={{borderRadius:20,paddingTop:30}} underlayColor="white" onPress={()=>{this.setState({Login:true})}}>
                    <Text style={{fontSize:15, color:'white'}}>Already Have an account/Login?</Text>
                    </TouchableOpacity>

                </View>
                )

                :(  <View style={{alignItems:"center",justifyContent:"space-around", padding:30}}>
                <TouchableOpacity onPress={()=>{console.log("SinUp")}} underlayColor="white"
                onPress={this.Login_user}
                >
                    <Text style={{fontSize:20, color:'white'}}>
                        Login
                    </Text>
                </TouchableOpacity>
                <TouchableOpacity style={{borderRadius:20,paddingTop:30,flex:0.01}} underlayColor="white" onPress={()=>{this.setState({Login:false})}}>
                <Text style={{fontSize:15, color:'white'}}>Create an account/SignUp?</Text>
                </TouchableOpacity>

            </View>)
            }


            </View>
        </KeyboardAvoidingView>)
}


}




const styles=StyleSheet.create({
    mainContainer:{

        flex:1,
        alignItems:"center",
        justifyContent:"center",
        backgroundColor:"#AFEEEE"

    },
    Inputcontaier:{
        alignItems:"center",
        justifyContent:"flex-start",
       flex:0.5,
       width:300,
        alignItems:"stretch",
        elevation:2,
        backgroundColor:"#00CED1",
        borderRadius:20,


    },
    InputField:{borderBottomColor:"white",
    borderBottomWidth:2,
    padding:4,
    flex:1 ,
    alignItems:"stretch", 
    justifyContent:"flex-start",
    color:'white',fontSize:20}

})
