import * as React from 'react';
import * as firebase from 'firebase'
import { createStackNavigator } from '@react-navigation/stack';
import HomeScreen from './screens/Home'
import Detail from './components/detail/Detail'
import AIcon from "react-native-vector-icons/AntDesign"
import FIcon from "react-native-vector-icons/FontAwesome"
import MIcon from 'react-native-vector-icons/MaterialCommunityIcons'
import {Text, Button , View} from 'react-native'
import Colors from './utils/Colors';
import SearchInput from './components/search/SearchInput'
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs'
import SignUp from './screens/Signup'

const Stack = createStackNavigator()
const Tab=createBottomTabNavigator()
function SearchStack(){
  return (
    <Stack.Navigator>
      <Stack.Screen name="Search" component={SearchInput} options={{headerMode:'none', headerShown:false}}/>
      <Stack.Screen name="Detail" component={Detail} options={{
         headerStyle:{
           backgroundColor:Colors.HeaderColor,},
           headerBackImage:()=>(      <AIcon name="back" size={32} color="white" />),
         headerTitle:({scene})=>(<Text style={{color:'white',fontSize:30}} numberOfLines={1}>Discription</Text>)
        }}/>
    </Stack.Navigator>
  )
}
const LogOut=()=>{
return (
  (<View><Button title="lou" onPress={()=>{firebase.auth().signOut().then(()=>{console.log("Suess")}).catch((error)=>{console.log(error.message)})}}></Button></View>)
);
}
function HomeStack() {
  return (

      <Stack.Navigator initialRouteName="Home">
    <Stack.Screen
          name="Home"
          component={HomeScreen}

          options={{headerMode:'none',headerShown:false,}}
        />
        <Stack.Screen name="Detail" component={Detail} options={{
         headerStyle:{
           backgroundColor:Colors.HeaderColor,

         },
         headerBackImage:()=>(<AIcon name="back" color="white" size={30}/>),

      headerTitle:<Text style={{color:'white',fontSize:30}} numberOfLines={1}>Discription</Text>
        }}/>

      </Stack.Navigator>


  );
}

export default function Main(){
  return(
  <Tab.Navigator initialRouteName='Home'  screenOptions={({ route }) => ({
    tabBarIcon: ({ focused, color, size }) => {
      let iconName;

      if (route.name === 'Home') {
        if(focused) { 
          return (<MIcon name={'home'} size={25} color={color} />); }
        else{  
           return <MIcon name={'home-outline'} size={25} color={color} />}
      } 
      else if (route.name === 'Search') {
       if(!focused) { return (<AIcon name={'search1'} size={25} color={color} />); }
       else{   return <FIcon name={'search'} size={25} color={color} />}
      }


    },

  })}
  tabBarOptions={{
    activeTintColor: 'white',
    inactiveTintColor: 'gray',
    style:{
      backgroundColor:Colors.HeaderColor
    },


  }} 
  >
    <Tab.Screen name='Home' component={HomeStack}  />
    <Tab.Screen name='Search' component={SearchStack} />
    <Tab.Screen name="LogOut" component={
     LogOut } />
  </Tab.Navigator>)
}
