import * as React from 'react'
import { View, Text, FlatList} from 'react-native'
import CategoryBox from "../components/home/CategoryBox"

import  Header from '../components/home/Header'
import * as firebase from 'firebase'


export default class HomeScreen extends React.Component{

    state={
        categories:[],
        loding:true,
        data_length:null
    }
    componentDidMount= ()=>{
        try{
            console.ignoredYellowBox = ['Setting a timer'];   
            let categories;
            const ref= firebase.database().ref('category')
            ref.on('value', snaps=>{
                if (snaps.val()){
                 categories=[];
                const data=Object.keys(snaps.val())



                this.setState({categories:data, loding:false, data_length:categories.length})
                }
            })
        }catch{(error)=>{
console.log(error)
        }}}


    render(){

        const {navigation:{navigate}}=this.props
        return (

            <View style={{flex:1,alignItems:"flex-start",justifyContent:"space-between",backgroundColor:"black"}}>
                <Header username="Bharat" />
                <View style={{flex:0.9}}>
                <FlatList 

                data={this.state.categories}
                renderItem={({item})=>{
                   return( <CategoryBox category={item} navigate={navigate}/>);
                }}
                keyExtractor={(item)=>(item)}
                ></FlatList></View>
            </View>
        );
    }
}
